function init(){setupModeButtons(),setupSquares(),reset()}function setupModeButtons(){for(var e=0;e<modeButtons.length;e++)modeButtons[e].addEventListener("click",function(){modeButtons[0].classList.remove("selected"),modeButtons[1].classList.remove("selected"),this.classList.add("selected"),numSquares="Easy"===this.textContent?3:6,reset()})}function setupSquares(){for(var e=0;e<squares.length;e++)squares[e].addEventListener("click",function(){var e=this.style.background;e===pickedColor?(messageDisplay.textContent="Correct!",resetButton.textContent="Play Again?",changeColors(e),h1.style.background=e):(this.style.background="#232323",messageDisplay.textContent="Try Again")})}function reset(){colors=generateRandomColors(numSquares),pickedColor=pickColor(),colorDisplay.textContent=pickedColor,resetButton.textContent="New Colors",messageDisplay.textContent="";for(var e=0;e<squares.length;e++)colors[e]?(squares[e].style.display="block",squares[e].style.background=colors[e]):squares[e].style.display="none";h1.style.background="steelblue"}function changeColors(e){for(var t=0;t<squares.length;t++)squares[t].style.background=e}function pickColor(){return colors[Math.floor(Math.random()*colors.length)]}function generateRandomColors(e){for(var t=[],o=0;o<e;o++)t.push(randomColor());return t}function randomColor(){return"rgb("+Math.floor(256*Math.random())+", "+Math.floor(256*Math.random())+", "+Math.floor(256*Math.random())+")"}var numSquares=6,colors=[],pickedColor,squares=document.querySelectorAll(".square"),colorDisplay=document.getElementById("colorDisplay"),messageDisplay=document.querySelector("#message"),h1=document.querySelector("h1"),resetButton=document.querySelector("#reset"),modeButtons=document.querySelectorAll(".mode");init(),resetButton.addEventListener("click",function(){reset()});